<?php  require_once("phplibs/PHPMailer/PHPMailerAutoload.php");  class Mail  {    public function __construct()    {    }    public function userActivation($name, $adresse, $link)    {      global $CONFIG;      $mail = new PHPMailer;      //$mail->SMTPDebug = 3;                               // Enable verbose debug output      $mail->isSMTP();                                      // Set mailer to use SMTP      $mail->Host = $CONFIG["MailHost"];  // Specify main and backup SMTP servers      $mail->SMTPAuth = true;                               // Enable SMTP authentication      $mail->Username = $CONFIG["MailUser"];                 // SMTP username      $mail->Password = $CONFIG["MailPasswort"];                           // SMTP password      $mail->SMTPSecure = 'tls';                            // Enable TLS encryption, `ssl` also accepted      $mail->Port = 587;                                    // TCP port to connect to      $mail->setFrom($CONFIG["MailAdresse"], $CONFIG["MailName"]);      $mail->addAddress($adresse, $name);     // Add a recipient      $mail->isHTML(true);                                  // Set email format to HTML      $mail->Subject = 'TerminGrul Benutzer Bestaetigung';      $mail->Body    = 'Hier der Link zum Verifizieren deiner Mailadresse:<br><a href="'.$link.'">TerminGrul Verifizierung</a>';      $mail->AltBody = 'Hier der Link zum Verifizieren deiner Mailadresse: '.$link;      $mail->send();    }  }?>